import React from "react";
import USAMap from "react-usa-map";
import { useNavigate } from "react-router-dom";

import "./index.css";

const CApp = () => {
  // const mapHandler = (event) => {
  //   const stateCode = event.target.dataset.name;
  //   setSelectedState(stateCode);
  // };
  const navigate = useNavigate();

  const mapHandler = (event) => {
    let stateName = event.target.innerHTML
      .replace("<title>", "")
      .replace("</title>", "");
    navigate("/state", {
      state: { stateName: stateName, stateCode: event.target.dataset.name },
    });
  };
  return (
    <div className="landing-page-container">
      {/* <h1>GraphQL State Explorer National Map</h1> */}
      <svg
        viewBox="0 0 200 200"
        height="350"
        width="800"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          fill="#bc7439"
          d="M63,-62.7C79.2,-46.7,88.4,-23.3,85.1,-3.3C81.9,16.8,66.1,33.5,49.8,42.9C33.5,52.2,16.8,54,-3.3,57.3C-23.4,60.7,-46.8,65.5,-55.7,56.1C-64.5,46.8,-58.8,23.4,-58.4,0.4C-58,-22.6,-62.9,-45.2,-54,-61.2C-45.2,-77.2,-22.6,-86.6,0.4,-87C23.3,-87.3,46.7,-78.7,63,-62.7Z"
          transform="translate(100 100)"
        />
        <text
          x="50%"
          y="50%"
          textAnchor="middle"
          dominantBaseline="middle"
          fill="white"
          fontSize="20"
        >
          GraphQL State Explorer
        </text>
        <text
          x="50%"
          y="60%"
          textAnchor="middle"
          dominantBaseline="middle"
          fill="white"
          fontSize="20"
        >
          National Map
        </text>
      </svg>
      <USAMap onClick={mapHandler} defaultFill="rgb(247, 166, 15)" />
    </div>
  );
};

export default CApp;

