import React from "react";
import ReactDOM from "react-dom";
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
} from "@apollo/client";

const client = new ApolloClient({
  uri: "http://localhost:4000/graphql", // Replace with your GraphQL server URL
  cache: new InMemoryCache(),
});

const GET_STATE_DATA = gql`
  query GetStateData($stateCode: String!) {
    usaMapData(stateCode: $stateCode) {
      id
      name
    }
  }
`;

function CApp() {
  const { loading, error, data } = useQuery(GET_STATE_DATA);

  if (loading) return React.createElement("p", null, "Loading...");

  if (error) return <p>Error: {error.message}</p>;

  return (
    <div>
      <h1>Users</h1>
      <ul>
        {data.usaMapData.map((user) => (
          <li key={user.id}>
            <p>Name: {user.name}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

function Root() {
  return (
    <ApolloProvider client={client}>
      <CApp />
    </ApolloProvider>
  );
}

export default Root;
