import React from "react";

import "./ParkDetails.css";
// import { useState, useEffect } from "react";

function ParkDetails({ park }) {
  // const [imageIdx, setImageIdx] = useState(0);
  // let parkImages = park.images;

  // This function iterates through the images provided by the API & displays each for 8s
  // useEffect(() => {
  //   setInterval(() => {
  //     setImageIdx((prevIdx) =>
  //       prevIdx === parkImages.length - 1 ? 0 : prevIdx + 1
  //     );
  //   }, 8000);
  // }, []);

  return (
    <div className="park-details">
      <h1>{park.name}</h1>

      <p>
        <a
          href={park.geocodes.link}
          target="_blank"
          rel="noopener noreferrer"
          className="official-site"
        >
          Official Site
        </a>
      </p>
      <p>Open: {park.closed_bucket}</p>
      <p>Address: {park.location.address}</p>
      <p>Locality: {park.location.locality}</p>
      <p>Postcode: {park.location.postcode}</p>
      <p>Region: {park.location.region}</p>
      <p>Latitude: {park.geocodes.main.latitude}</p>
      <p>Longitude: {park.geocodes.main.longitude}</p>
      {/* {parkImages[imageIdx] === undefined ? (
        <p>Image not available.</p>
      ) : (
        <figure>
          <img
            src={parkImages[imageIdx].url}
            alt={parkImages[imageIdx].title}
            className="park-image"
            loading="lazy"
          />
          <figcaption>
            <i>{parkImages[imageIdx].caption}</i>
          </figcaption>
        </figure>
      )} */}
    </div>
  );
}

export default ParkDetails;
